const items = $input.all();

if (items.length === 0) {
  return [{ json: { formattedMessage: 'This folder is empty.' } }];
}

let message = 'Files found:\n';
for (const item of items) {
  message += `- ${item.json.name}\n`;
}

return [{ json: { formattedMessage: message } }];

const items = $input.all();
const updatedItems = items.map((item) => {
  const fullPath = item?.json?.data?.body;
  const pathParts = fullPath.split("/");
  const fileName = pathParts.pop();
  const folderName = pathParts.pop();
  return { fileName, folderName };
});
return updatedItems;

const items = $input.all();
const command = items[0]?.json?.data?.body?.trim();

// Match using a regex pattern:
// - starts with action (word)
// - then a source path: /some folder/somefile
// - then a destination path: /some folder
const match = command.match(/^(\w+)\s+\/(.+\/[^\/\s]+)\s+\/(.+)$/);

if (!match) {
  throw new Error("Invalid command format");
}

const action = match[1];                 // "MOVE"
const sourcePath = match[2];             // "PBL WSMA/README.md"
const destinationFolder = match[3];      // "Project docs"

// Parse source path
const lastSlash = sourcePath.lastIndexOf("/");
const folder1 = sourcePath.substring(0, lastSlash);  // "PBL WSMA"
const file = sourcePath.substring(lastSlash + 1);    // "README.md"

const folder2 = destinationFolder; // already clean

return [
  {
    json: {
      action,
      folder1,
      file,
      folder2
    }
  }
];

